(74, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(75, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(76, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(77, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(78, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(79, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(80, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(81, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(82, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(83, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', 'info', '{}', 'pending', 'woocommerce.com', '2025-06-26 15:54:50', NULL, 0, 'plain', '', 0),
(84, 'woohoo10-coupon', 'marketing', 'en_US', 'Celebrate and save with WooCommerce 10.0', 'To mark this milestone release, get 10% off extensions and themes in the official Woo Marketplace. Use code WOOHOO10 at checkout to save on essential tools that help grow your business. Offer ends August 15, 2025. Terms and conditions apply.', 'info', '{}', 'unactioned', 'woocommerce.com', '2025-08-11 02:15:22', NULL, 0, 'plain', '', 0);
